import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { compose } from 'recompose';

import { Container, Col, Row, Form, Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.css';
import app from 'firebase/app';
import 'firebase/firestore';
import { withAuthorization } from '../Session';
import './Home.css';
import { withFirebase } from '../Firebase';

const HomePage = () => (
  <div>
    <HomePageForm/>
  </div>
)
const INITIAL_STATE = {
  name: '',
  num_party: '',
  notes: '',
};

class HomePageBase extends Component{
  constructor(props) {
    super(props);
    this.state = { ...INITIAL_STATE };
    this.db = app.firestore();
  }
  onSubmit = event => {
    const { name, num_party, notes } = this.state;
    var attending = document.getElementById("attending").checked;
    this.db.collection("attendants").doc(name).set({
      name,
      num_party,
      attending,
      notes
    }).then(() => {console.log("success")})
    .catch(e => {console.log("ERROR:"+e)});
    event.preventDefault();
  };
  onChange = event => {
    console.log(event.target.name)
    this.setState({ [event.target.controlID]: event.target.value});
  };
  render() {
    const { name, num_party, attending, notes } = this.state;
    return (

      <Form onSubmit={this.onSubmit}>
        <Form.Group as={Col} controlID="name">
          <Form.Label>Name</Form.Label>
          <Form.Control type="text" placeholder="Full Name" onChange={this.onChange}/>
        </Form.Group>

        <Form.Group as={Col} controlID="num_party">
          <Form.Label>Number in Party</Form.Label>
          <Form.Control type="number" placeholder="0" min="0" onChange={this.onChange}/>
        </Form.Group>


        <Form.Group as={Col} controlId="formGridState">
          <Form.Label>Attending</Form.Label>
          <Form.Control as="select">
            <option>Choose...</option>
            <option>Yes</option>
            <option>No</option>
          </Form.Control>
        </Form.Group>

        <Form.Group as={Col} controlId="exampleForm.ControlTextarea1">
          <Form.Label>Notes for Bride and Groom</Form.Label>
          <Form.Control as="textarea" rows="3" onChange={this.onChange}/>
        </Form.Group>

        <div className="col-12 text-right">
          <Button className="btn" variant="primary" type="submit" >
            Submit
          </Button>
        </div>
      </Form>
    );
  }
}
const condition = authUser => !!authUser;
const HomePageForm = compose(
  withAuthorization(condition),
  withRouter,
  withFirebase,
)(HomePageBase);
export default HomePage;
export {HomePageForm};


