import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { compose } from 'recompose';

import { Container, Col, Row } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.css';
import app from 'firebase/app';
import 'firebase/firestore';
import { withAuthorization } from '../Session';
import './Home.css';
import { withFirebase } from '../Firebase';

const HomePage = () => (
  <div>
    <HomePageForm/>
  </div>
)
const INITIAL_STATE = {
  name: '',
  num_party: '',
  notes: '',
};

class HomePageBase extends Component{
  constructor(props) {
    super(props);
    this.state = { ...INITIAL_STATE };
    this.db = app.firestore();
  }
  onSubmit = event => {
    const { name, num_party, notes } = this.state;
    var attending = document.getElementById("attending").checked;
    this.db.collection("attendants").doc(name).set({
      name,
      num_party,
      attending,
      notes
    }).then(() => {console.log("success")})
    .catch(e => {console.log("ERROR:"+e)});
    event.preventDefault();
  };
  onChange = event => {
    this.setState({ [event.target.name]: event.target.value});
  };
  render() {
    const { name, num_party, attending, notes } = this.state;
    return (

      <Container style={{
        marginLeft:'30px',
      }}>
        <form onSubmit={this.onSubmit} className="row">
          <Row>
            <Col>
              <h3>Name:</h3>
              <input
                name="name"
                value={name}
                onChange={this.onChange}
                type="text"
                placeholder="Full Name"
              />
            </Col>
            <Col>
              <h3>Number in Party:</h3>
              <input
                name="num_party"
                value={num_party}
                onChange={this.onChange}
                type="number"
                min="0"
                placeholder="Number in Party"
            />
            </Col>
            <Col>

              <h3>Attending:</h3>
              <input
                id="attending"
                value={attending}
                onChange={this.onChange}
                type="checkbox"
              />
            </Col>

          </Row>
          <Row>
            {/* <Col>
              <h3>Notes:</h3>
              <textarea
                name="notes"
                value={notes}
                onChange={this.onChange}
                type="text"
                style={{
                  rows: 50,
                  cols: 1000,
                  width: '50%',
                  height: '50%'
                }}
                placeholder="Notes for Bride and Groom"
              />
            </Col> */}
            <Col>
              <input type="submit" value="Submit"></input>
            </Col>
          </Row>
        </form>
      </Container>
    );
  }
}
const condition = authUser => !!authUser;
const HomePageForm = compose(
  withAuthorization(condition),
  withRouter,
  withFirebase,
)(HomePageBase);
export default HomePage;
export {HomePageForm};
